mapper.update:
UPDATE
	album
SET
	image=#{image}
WHERE
	id=#{id}

UPDATE
	album
SET
	play_count=#{playCount}
WHERE
	id=#{id}

mapper.select:
SELECT alb.id, alb.name, alb.artist_id, alb.region, alb.style, alb.intro, alb.play_count, alb.release_date, alb.image, art.name artist_name FROM album as alb LEFT JOIN artist as art ON alb.artist_id=art.id
AND alb.region=#{region}
AND alb.style=#{style}
ORDER BY alb.play_count DESC LIMIT #{offset}, #{count}

SELECT COUNT(*) FROM album as alb
AND alb.region=#{region}
AND alb.style=#{style}

SELECT alb.id, alb.name, alb.artist_id, alb.region, alb.style, alb.intro, alb.play_count, alb.release_date, alb.image, art.name artist_name FROM ( SELECT * FROM album
AND album.region=#{region}
ORDER BY release_date DESC LIMIT 0, #{count} ) as alb LEFT JOIN artist as art ON alb.artist_id=art.id
